<div class="flex flex-col flex-shrink-0 w-full space-y-8 text-black-500">
    <div>
        [text* FirstName class:form-field placeholder "First name"]
    </div>
    <div>
        [text* LastName class:form-field placeholder "Last name"]
    </div>
    <div>
        [email* Email class:form-field placeholder "Email"]
    </div>
    <div>
        [tel* ContactNumber class:form-field placeholder "Contact Number"]
    </div>
    <div>
        [textarea* Message x2 class:form-field placeholder "Message"]
    </div>
    <div class="m-0">
        [submit class:btn-primary "Book Now"]
    </div>
</div>